mapper.select:
SELECT tudi.identify_college_id AS identifyCollegeId, tudi.user_id AS userId, tudi.college_no AS collegeNo, tudi.institute_no AS instituteNo, tudi.entrance_time AS entranceTime, tudi.graduation_time AS graduationTime, tudi.degree_no AS degreeNo, tudi.profession_name AS professionName, tudi.identify_status AS identifyStatus, tudi.student_no AS studentNo, tudi.id_card_no AS idCardNo, tudi.identify_type AS identifyType, tudi.grade AS grade, tui.user_real_name AS studentName, tui.gender AS gender, tui.province AS province, tui.city AS city FROM t_user_degree_identify AS tudi LEFT JOIN t_user_info AS tui ON tudi.user_id=tui.user_id
AND tudi.college_no = #{collegeNo,jdbcType=VARCHAR} AND tudi.enabled = 1
AND tudi.institute_no = #{instituteNo,jdbcType=BIGINT}
AND tui.gender = #{gender,jdbcType=TINYINT}
AND tudi.degree_no = #{degreeNo,jdbcType=TINYINT}
AND tudi.identify_type = #{identifyType,jdbcType=TINYINT}
AND tudi.identify_status = #{identifyStatus,jdbcType=TINYINT}
AND tui.user_real_name = #{studentName,jdbcType=VARCHAR}
AND tudi.grade = #{grade,jdbcType=VARCHAR}
AND tudi.identify_college_id = #{identifyCollegeId,jdbcType=BIGINT}
